function burgerMenu(){const e=document.querySelector(".burger"),i=document.querySelector(".menu"),s=document.querySelector("body");e.addEventListener("click",(()=>{i.classList.contains("active")?(i.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked")):(i.classList.add("active"),e.classList.add("active-burger"),s.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>767.98&&(i.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav);const swiper=new Swiper(".swiper",{direction:"horizontal",loop:!1,speed:800,pagination:{el:".swiper-pagination",clickable:!0},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},mousewheel:{sensitivity:1},autoplay:{delay:5e3,stopOnLastListe:!0,disableOnInteraction:!0}});